options.css:
* {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  margin: 0;
  padding: 0;
}

.dashboard-header {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 12px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  padding: 20px;
}

.logo-container {
  display: flex;
  align-items: center;
  margin-right: 20px;
}

.logo-svg {
  width: 60px;
  height: 60px;
  margin-right: 15px;
}

.title-section {
  text-align: center;
  color: white;
}

.main-title {
  font-size: 28px;
  font-weight: 700;
  margin-bottom: 8px;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
}

.subtitle {
  font-size: 16px;
  font-weight: 400;
  opacity: 0.9;
  margin-bottom: 12px;
}

.time-display {
  font-size: 14px;
  font-weight: 300;
  opacity: 0.8;
  background: rgba(255, 255, 255, 0.1);
  padding: 8px 16px;
  border-radius: 20px;
  backdrop-filter: blur(10px);
}

@media (max-width: 768px) {
  .dashboard-header {
    flex-direction: column;
    text-align: center;
  }

  .logo-container {
    margin-right: 0;
    margin-bottom: 15px;
  }

  .main-title {
    font-size: 24px;
  }
}

options.onInit:
// Initialize time display
function updateTime() {
  const timeElement = htmlNode.getElementById('current-time');
  if (timeElement) {
    const now = new Date();
    const timeString = now.toLocaleString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
    timeElement.textContent = timeString;
  }
}

// Update time immediately and then every second
updateTime();
setInterval(updateTime, 1000);
