options.css:
* {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  margin: 0;
  padding: 0;
}

.dashboard-header {
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 100%;
  background: transparent;
  padding: 8px 6px;
  text-align: center;
  overflow: hidden;
}

.logo-container {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 12px;
  flex-shrink: 0;
  background: linear-gradient(135deg, #4A90E2 0%, #2E5A8F 100%);
  border-radius: 50%;
  width: 60px;
  height: 60px;
  box-shadow: 0 4px 16px rgba(74, 144, 226, 0.4);
}

.logo-svg {
  width: 40px;
  height: 40px;
  filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3));
}

.title-section {
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.main-title {
  font-size: 16px;
  font-weight: 700;
  color: white;
  background: linear-gradient(135deg, #4A90E2 0%, #2E5A8F 100%);
  padding: 8px 12px;
  border-radius: 20px;
  box-shadow: 0 4px 16px rgba(74, 144, 226, 0.3);
  line-height: 1.2;
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
}

.subtitle {
  font-size: 11px;
  font-weight: 400;
  color: white;
  background: linear-gradient(135deg, #2E5A8F 0%, #1E3A5F 100%);
  padding: 6px 10px;
  border-radius: 16px;
  box-shadow: 0 3px 12px rgba(46, 90, 143, 0.3);
  line-height: 1.2;
}

.time-display {
  font-size: 10px;
  font-weight: 300;
  color: white;
  background: linear-gradient(135deg, #1E3A5F 0%, #0F1D2F 100%);
  padding: 5px 8px;
  border-radius: 14px;
  box-shadow: 0 2px 8px rgba(30, 58, 95, 0.3);
  display: inline-block;
  line-height: 1.1;
  word-wrap: break-word;
  max-width: 100%;
}

options.onInit:
// Initialize time display and title
function updateTime() {
  const timeElement = htmlNode.getElementById('current-time');
  if (timeElement) {
    const now = new Date();
    const timeString = now.toLocaleString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
    timeElement.textContent = timeString;
  }
}

function updateTitle() {
  const titleElement = htmlNode.getElementById('dashboard-title');
  if (titleElement) {
    try {
      // Use getTemplateSrv().replace() to get the variable value - this is the proper way
      const dashboardTitle = getTemplateSrv().replace('${dashboard_title}') || 'CVAD Overall Dashboard';
      titleElement.textContent = dashboardTitle;
    } catch (e) {
      titleElement.textContent = 'CVAD Overall Dashboard';
    }
  }
}

function updateSubtitle() {
  const subtitleElement = htmlNode.getElementById('dashboard-subtitle');
  if (subtitleElement) {
    try {
      // Use getTemplateSrv().replace() to get the subtitle variable value
      const dashboardSubtitle = getTemplateSrv().replace('${dashboard_subtitle}') || 'Comprehensive System Monitoring & Management';
      subtitleElement.textContent = dashboardSubtitle;
    } catch (e) {
      subtitleElement.textContent = 'Comprehensive System Monitoring & Management';
    }
  }
}

// Update time immediately and then every second
updateTime();
setInterval(updateTime, 1000);

// Update title and subtitle when panel initializes
updateTitle();
updateSubtitle();

// Listen for panel updates to refresh the title and subtitle when variables change
htmlNode.addEventListener('panelupdate', () => {
  updateTitle();
  updateSubtitle();
});
