options.css:
* {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  margin: 0;
  padding: 0;
}

.dashboard-header {
  display: flex;
  align-items: center;
  height: 120px;
  background: linear-gradient(135deg, #4A90E2 0%, #2E5A8F 50%, #1E3A5F 100%);
  border-radius: 12px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.15);
  padding: 12px;
}

.logo-container {
  display: flex;
  align-items: center;
  margin-right: 20px;
  flex-shrink: 0;
}

.logo-svg {
  width: 120px;
  height: 120px;
  filter: drop-shadow(0 4px 8px rgba(0, 0, 0, 0.3));
}

.title-section {
  text-align: left;
  color: white;
  flex-grow: 1;
}

.main-title {
  font-size: 24px;
  font-weight: 700;
  margin-bottom: 8px;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
}

.subtitle {
  font-size: 14px;
  font-weight: 400;
  opacity: 0.9;
  margin-bottom: 12px;
}

.time-display {
  font-size: 13px;
  font-weight: 300;
  opacity: 0.8;
  background: rgba(255, 255, 255, 0.15);
  padding: 6px 12px;
  border-radius: 18px;
  backdrop-filter: blur(10px);
  display: inline-block;
}

@media (max-width: 768px) {
  .dashboard-header {
    flex-direction: column;
    text-align: center;
  }

  .logo-container {
    margin-right: 0;
    margin-bottom: 20px;
  }

  .title-section {
    text-align: center;
  }

  .main-title {
    font-size: 24px;
  }
}

options.onInit:
// Initialize time display and title
function updateTime() {
  const timeElement = htmlNode.getElementById('current-time');
  if (timeElement) {
    const now = new Date();
    const timeString = now.toLocaleString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
    timeElement.textContent = timeString;
  }
}

function updateTitle() {
  const titleElement = htmlNode.getElementById('dashboard-title');
  if (titleElement) {
    try {
      // Use getTemplateSrv().replace() to get the variable value - this is the proper way
      const dashboardTitle = getTemplateSrv().replace('${dashboard_title}') || 'CVAD Overall Dashboard';
      titleElement.textContent = dashboardTitle;
    } catch (e) {
      titleElement.textContent = 'CVAD Overall Dashboard';
    }
  }
}

function updateSubtitle() {
  const subtitleElement = htmlNode.getElementById('dashboard-subtitle');
  if (subtitleElement) {
    try {
      // Use getTemplateSrv().replace() to get the subtitle variable value
      const dashboardSubtitle = getTemplateSrv().replace('${dashboard_subtitle}') || 'Comprehensive System Monitoring & Management';
      subtitleElement.textContent = dashboardSubtitle;
    } catch (e) {
      subtitleElement.textContent = 'Comprehensive System Monitoring & Management';
    }
  }
}

// Update time immediately and then every second
updateTime();
setInterval(updateTime, 1000);

// Update title and subtitle when panel initializes
updateTitle();
updateSubtitle();

// Listen for panel updates to refresh the title and subtitle when variables change
htmlNode.addEventListener('panelupdate', () => {
  updateTitle();
  updateSubtitle();
});
